// Generator: Creates +1, +2, +3 variants of ALL armor types
function generateMagicArmor(baseArmorList) {
    let magicArmor = [];
    
    // Filter out shields because they have their own naming conventions usually, 
    // but you can include them if you want generic "Shield +1" generated again.
    // For now, let's just do the suits.
    const suits = baseArmorList.filter(a => a.armor_type !== "Shield");

    suits.forEach(a => {
        for (let i = 1; i <= 3; i++) {
            magicArmor.push({
                ...a, // Copy original stats (Name, AC, Type)
                name: `${a.name} +${i}`,
                category: "Magic Armor",
                ac: typeof a.ac === 'number' ? a.ac + i : a.ac, // Add bonus to AC
                desc: `${a.desc} | +${i} to AC. Magical.`
            });
        }
    });
    return magicArmor;
}

// Generator: Creates +1, +2, +3 variants of weapons automatically
function generateMagicWeapons(weapons) {
    let magicWeapons = [];
    weapons.forEach(w => {
        for (let i = 1; i <= 3; i++) {
            magicWeapons.push({
                ...w, 
                name: `${w.name} +${i}`,
                category: "Magic Weapon",
                attack_bonus: i, 
                damage_bonus: i,
                desc: `${w.desc} | +${i} Magical Weapon`
            });
        }
    });
    return magicWeapons;
}

// Combine into your main list
export const ALL_ACTIONS = [
    ...ALL_WEAPONS,
    ...generateMagicWeapons(ALL_WEAPONS), // Fixed Reference
    ...ALL_ARMOR, 
    ...generateMagicArmor(ALL_ARMOR),      // The new armor generator
    ...ALL_ITEMS, 
    ...ALL_SPELLS, 
    ...ALL_FEATURES
];

